SELECT (SELECT stat_value FROM SP_IQSTATISTICS('ConnectionsActive')) AS ConnectionsActive, (SELECT stat_value FROM SP_IQSTATISTICS('OperationsActive')) AS OperationsActive, (SELECT stat_value FROM SP_IQSTATISTICS('OperationsWaiting')) AS OperationsWaiting, (SELECT stat_value FROM SP_IQSTATISTICS('OperationsActiveLoadTableStatements')) AS OperationsActiveLoadTableStatements FROM DUMMY